import React from 'react';
import { useState , useEffect} from 'react';

const App = () => {

  var [facList,setFacList] = useState([])
  var [nSlots,setnSLots] = useState([])
  var [defFac,setdefFac] = useState(0)

  useEffect(() => {
     fetch("http://localhost:8080/")
    .then(response => response.json())
    .then((data) => setFacList(data))


  },[facList])

  return (
    <div>

      <p>Set Default Max Entries Value</p>
      <select onChange={
        async (e) => {
          const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({value: e.target.value })
          };

          await fetch(`http://localhost:8080/facDefault`, requestOptions)
          .then(data => console.log(data));

          setFacList([])
        }
      }>
        <option value="0">0</option>
        <option value="1">1</option>
        <option value="2">2</option>
        <option value="3">3</option>
        <option value="4">4</option>

      </select>


      <table className="faculty" border="2" cellSpacing="0" cellPadding="6">
        <thead>
          <tr>
              <th>Faculty</th>
              <th>Max Entries</th>
          </tr>
        </thead>

        <tbody>
          {facList.map((fac) => <Faculty key={fac._id} id={fac._id} name={fac.name} entries={fac.maxEntries} />)}



        </tbody>

      </table>










      <button onClick={()=>{

      const requestOptions = {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ faculty: "Enter Name", maxEntries: "0" })
      };

      fetch(`http://localhost:8080/`, requestOptions)
      .then(data => console.log(data));

      setFacList([])


              }}>
                Add new faculty
            </button>


            <button onClick={async () => {
                        const requestOptions = {
                          method: 'POST',
                          headers: { 'Content-Type': 'application/json' },
                          body: JSON.stringify({value: '0' })
                        };

                        await fetch(`http://localhost:8080/facDefault`, requestOptions)
                        .then(data => console.log(data));

                        setFacList([])
                      }
            }>Reset Max Entries</button>


            <p>Enter the number of slots</p>
            <input type="number" max="20" onChange={async (e) => {
                setnSLots(Array.from(Array(Number(e.target.value)).keys()));
                console.log(nSlots);




              }}>


            </input>
            <br></br>
            <label>Enter Default Number of Faculties</label>
            <select onChange={(e)=>{
              setdefFac(e.target.value)
            }}>
              <option value="0">0</option>
              <option value="1">1</option>
              <option value="2">2</option>
              <option value="3">3</option>

            </select>


              {nSlots.map((slots) => <Slot defFac={defFac} key={slots+1} id={slots+1} />)}


            <button>Submit</button>
            </div>


            )
          }



export const Slot = (props) => {
  return (
    <div>

      <h1>Slot {props.id}</h1>

      <table className="slot" border="2" cellspacing="0" cellpadding="2">
        <tr>
          <th>Room</th>
          <th>Faculties</th>
        </tr>

        <tr>
          <td><input value="IF3"></input></td>
          <td><input value={props.defFac}></input></td>
        </tr>

        <tr>
          <td><input value="IF4"></input></td>
          <td><input value={props.defFac}></input></td>
        </tr>

        <tr>
          <td><input value="IS4"></input></td>
          <td><input value={props.defFac}></input></td>
        </tr>

        <tr>
          <td><input value="IS5"></input></td>
          <td><input value={props.defFac}></input></td>
        </tr>
      </table>

    </div>
  )
}




const Faculty = (props) => {
  return (
    <tr>

      <th><input id={props.id} type="text"  defaultValue={props.name} onChange= {async(e) =>{

        console.log(e.target.value)
        const requestOptions = {
            method: 'PUT',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ id:props.id, name: e.target.value, maxEntries: props.entries })
        };
        await fetch(`http://localhost:8080/`, requestOptions)
            .then(data => console.log(data));

      } 

      }></input></th>


      <td><input id={props.id} type="text"  defaultValue={props.entries} onChange= {async (e) =>{

        console.log(e.target.value)
        const requestOptions = {
            method: 'PUT',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ id:props.id, name: props.name, maxEntries: e.target.value })
        };
        await fetch(`http://localhost:8080/`, requestOptions)
            .then(data => console.log(data));

      } 

      }></input>

      <button onClick={async()=>{
        var requestOptions = {
          method: 'DELETE'
        }

        await fetch(`http://localhost:8080/${props.name}`, requestOptions)
        .then(data => console.log(data));

      }}>DeleteIcon</button>


        </td>

    </tr>
  )
}



export default App
