import React from 'react';
import { useState , useEffect} from 'react';

const App = () => {

  var [facList,setFacList] = useState([])

  useEffect(() => {
    fetch("http://localhost:8080/")
    .then(response => response.json())
    .then((data) => setFacList(data))


  },[facList])

  return (
    <div>

      <table border="2" cellSpacing="0" cellPadding="6">
        <thead>
          <tr>
              <th>Faculty</th>
              <th>Max Entries</th>
          </tr>
        </thead>

        <tbody>
          {facList.map((fac) => <Faculty key={fac._id} id={fac._id} name={fac.name} entries={fac.maxEntries} />)}



        </tbody>

      </table>

      <button onClick={()=>{

      const requestOptions = {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ faculty: "Enter Name", maxEntries: "0" })
      };

      fetch(`http://localhost:8080/`, requestOptions)
      .then(data => console.log(data));

      setFacList([])


              }}>
            </button>

    </div>
  )
}

const Faculty = (props) => {
  return (
    <tr>
      <th>{props.name}</th>
      <td><input id={props.id} type="text"  defaultValue={props.entries} onChange= {(e) =>{

        console.log(e.target.value)
        const requestOptions = {
            method: 'PUT',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ faculty: props.name, maxEntries: e.target.value })
        };
        fetch(`http://localhost:8080/`, requestOptions)
            .then(data => console.log(data));

      } 

      }></input></td>

    </tr>
  )
}



export default App
